import React, { useState } from 'react'

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();
today = mm + '/' + dd + '/' + yyyy;

const InputFormKurir = () => {
  const [state, setState] = useState({
    kurir: "",
    tanggal: "",
    kecepatan: "",
    jumlah: ""
  });

  const updateState = (e) => {
    const {name, value} = e.target;
    let newState = state;
    newState[name] = value;
    console.log(newState);
    setState(newState);
  }

  return (
    <>
      <label htmlFor="nama-kurir" className="input-label">Nama Kurir</label>
      <input id="nama-kurir" type="text" value={state.kurir} required onChange={updateState}/>
      <label htmlFor="tanggal" className="input-label" >Tanggal Pengiriman</label>
      <input id="tanggal" type="date" name="tanggal" value={state.tanggal} required min={today} onChange={updateState}/>
      <label htmlFor="kecepatan" className="input-label">Kecepatan Rata-Rata Kurir</label>
      <input id="kecepatan" type="text" name="kecepatan" value={state.kecepatan} required onChange={updateState}/>
      <label htmlFor="Jumlah Lokasi" className="input-label">Jumlah Lokasi</label>
      <input id="jumlah-lokasi" type="number" name="jumlah-lokasi" value={state.jumlah} required min="2" onChange={updateState}/>
      <button className="input-btn">Next</button>
    </>
  )
}

export default InputFormKurir
