import React, { useState } from 'react'

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();
today = mm + '/' + dd + '/' + yyyy;

const InputFormKurir = ({updateDataKurir}) => {
  const [dataKurir, setDataKurir] = useState({
    kurir: "",
    tanggal: "",
    kecepatan: "",
    jumlah: ""
  });

  const updateState = (e) => {
    const {name, value} = e.target;
    let newDataKurir = dataKurir;
    newDataKurir[name] = value;
    setDataKurir(newDataKurir);
  }

  // const [kurir, setKurir] = useState("");
  // const [tanggal, setTanggal] = useState("");
  // const [kecepatan, setKecepatan] = useState("");
  // const [jumlah, setJumlah] = useState("");

  // const updateKurir = (e) => {
  //   setKurir(e.target.value);
  // }
  
  // const updateTanggal = (e) => {
  //   setTanggal(e.target.value);
  // }
  
  // const updateKecepatan = (e) => {
  //   setKecepatan(e.target.value);
  // }
  
  // const updateJumlah = (e) => {
  //   setJumlah(e.target.value);
  // }

  return (
    <>
      <label htmlFor="nama-kurir" className="input-label">Nama Kurir</label>
      <input id="nama-kurir" type="text" name="kurir" value={dataKurir.kurir} required onChange={updateState}/>
      <label htmlFor="tanggal" className="input-label" >Tanggal Pengiriman</label>
      <input id="tanggal" type="date" name="tanggal" value={dataKurir.tanggal} required min={today} onChange={updateState}/>
      <label htmlFor="kecepatan" className="input-label">Kecepatan Rata-Rata Kurir</label>
      <input id="kecepatan" type="text" name="kecepatan" value={dataKurir.kecepatan} required onChange={updateState}/>
      <label htmlFor="Jumlah Lokasi" className="input-label">Jumlah Lokasi</label>
      <input id="jumlah-lokasi" type="number" name="jumlah" value={dataKurir.jumlah} required min="2" onChange={updateState}/>
      {/* <button className="input-btn" onSubmit={() => updateDataKurir()}>Next</button> */}
    </>
  )
}

export default InputFormKurir
