import React, {useState} from 'react'

const InputFormLokasi = ({idx}) => {
  const [namaLokasi, setNamaLokasi] = useState("");
  const [longitude, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  
  const updateNamaLokasi = (e) => {
    setNamaLokasi(e.target.value);
  }
  
  const updateLongitude = (e) => {
    setLongitude(e.target.value);
  }
  
  const updateLatitude = (e) => {
    setLatitude(e.target.value);
  }
  return (
    <div>
       <form className="input-form" onSubmit={updateData}>
        <label htmlFor="nama-lokasi" className="input-label">Nama Lokasi {idx} {idx === 1 ? <>Lokasi Asal</> : ''}</label>
        <input id="nama-lokasi" type="text" name="lokasi" value={namaLokasi} required onChange={updateNamaLokasi}/>
        <label htmlFor="longitude" className="input-label">Nama Lokasi</label>
        <input id="longitude" type="text" name="longitude" value={longitude} required onChange={updateLongitude}/>
        <label htmlFor="latitude" className="input-label">Nama Lokasi</label>
        <input id="latitude" type="text" name="latitude" value={latitude} required onChange={updateLatitude}/>
        <button className="input-btn">Next</button>
      </form>
    </div>
  )
}

export default InputFormLokasi
